        [TestMethod()]
        public void GoogleSearchScraperTest()
        {
            var mockHtmlRequestor = new Mock<IHTMLRequestor>();
            mockHtmlRequestor.Setup(x => x.GetHtmlAsync(It.IsAny<string>())).Returns(Task.FromResult(exampleHTML));
            var scraper = new GoogleSearchScraper(mockHtmlRequestor.Object);

            var result = scraper.GetSEOResults("land registry search", "infotrack.co.uk");

            mockHtmlRequestor.Verify(x => x.GetHtmlAsync("https://www.google.co.uk/search?num=100&q=land+registry+search"), Times.Once);
            Assert.AreEqual(SearchProvider.Google.ToString(), result.SearchProvider);
            Assert.AreEqual("land registry search", result.SearchString);
            Assert.AreEqual("infotrack.co.uk", result.TargetURL);
            CollectionAssert.AreEqual(new List<int>() { 13 }, result.Occurrences);
        }
